    return (
      <div className="board-row">
        {renderTile(i, 0)}
        {renderTile(i, 1)}
        {renderTile(i, 2)}
        {renderTile(i, 3)}
      </div>
    );
  }
  function updateBoard() {
    setTiles(newNum(tiles));
    console.log(tiles);
  }

  useEffect(() => {
    setTiles = updateBoard();
  }, []);

  return (
    <div>
      <div className="status">{status}</div>
