function useKeyPress() {
  // State for keeping track of whether key is pressed
  const [keyPressed, setKeyPressed] = useState(false);

  function move({ key }) {
    let pressed = "";
    switch (key) {
      case "k":
        pressed = "up";
        break;
      case "j":
        pressed = "down";
        break;
      case "h":
        pressed = "left";
        break;
      case "l":
        pressed = "right";
        break;
      default:
        pressed = "";
        break;
    }
    console.log(`pressed ${pressed}`);
