import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Tile(props) {
	return (
		<button 
			className="tile" 
			onClick={props.onClick}
		>
			{props.value}
		</button>
	);
}


// create empty board

class Board extends React.Component {
	//board logic and values, empty at init
	createBoard(){
		let dimension = 4
		let board = Array(dimension)
		board.fill(Array(dimension).fill(2))
		return board
	}
	renderTile(i, j) {
		return (
			<Tile 
				value={this.state.tiles[i][j]}
				onClick={() => this.handleClick(i)}
			/>
		);
	}
	constructor(props) {
		super(props);
		this.state = {
			tiles: this.createBoard(),
			gameOver: false,
		};
	}
	handleClick(i) {
		const tiles = this.state.tiles.slice();

		//ignore click if game has been won
		//if (calculateWinner(tiles) || tiles[i]) {
			//return;
		//}
		//tiles[i] = this.state.xIsNext ? 'X' : 'O';
		this.setState({
			tiles: tiles,
			gameOver: gameOver(), 
			//xIsNext: !this.state.xIsNext,
		});
	}
	renderTile(i) {
		return (
			<Tile 
				value={this.state.tiles[i]}
				onClick={() => this.handleClick(i)}
			/>
		);
	}

	//createBoard() {
		//let board = []
		//for (let i =0; i < 4; i++) {
			//let row=[]
			//for (let j=0; j < 4; j++) {
				//row.push(this.renderTile(i))
			//}
			//board.push(<div>{row}</div>)	
		//}
		//return board
	//}

	//render() {
		//return (
			//<div>
				//{this.createBoard}
			//</div>
		//)
	//}

	render() {
		let status = true;
		//const winner = calculateWinner(this.state.tiles);
		//let status;
		//if (winner) {
			//status = 'Winner: ' + winner;
		//} else {
			//status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O');
		//}


		return (
			<div>
				<div className="status">{status}</div>
				<div className="board-row">
					{this.renderTile(0)}
					{this.renderTile(1)}
					{this.renderTile(2)}
					{this.renderTile(3)}
				</div>
				<div className="board-row">
					{this.renderTile(4)}
					{this.renderTile(5)}
					{this.renderTile(6)}
					{this.renderTile(7)}
				</div>
				<div className="board-row">
					{this.renderTile(8)}
					{this.renderTile(9)}
					{this.renderTile(10)}
					{this.renderTile(11)}
				</div>
				<div className="board-row">
					{this.renderTile(12)}
					{this.renderTile(13)}
					{this.renderTile(14)}
					{this.renderTile(15)}
				</div>
			</div>
		);
	}
}

class Game extends React.Component {
	render() {
		return (
			<div className="game">
				<div className="game-board">
					<Board />
				</div>
				<div className="game-info">
					<div>{/* status */}</div>
					<ol>{/* TODO */}</ol>
				</div>
			</div>
		);
	}
}

// return True if game over
function gameOver() {
	return false;
}

